export const initialState = {
  basket: [
    {
      id: "7423423",
      title:
        "lorem10Culpa elit voluptate deserunt duis sunt adipisicing sit esse minim aute sint.orem10Culpa elit voluptate deserunt",
      price: 1134.96,
      rating: 4,
      image:
        "https://images-na.ssl-images-amazon.com/images/I/71-2SRq1bKL._AC_SX679_.jpg",
    },
    {
      id: "8532",
      title:
        "lorem10Culpa elit voluptate deserunt duis sunt adipisicing sit esse minim aute sint.orem10Culpa elit voluptate deserunt",
      price: 1134.96,
      rating: 4,
      image:
        "https://images-na.ssl-images-amazon.com/images/I/71-2SRq1bKL._AC_SX679_.jpg",
    },
  ],
  user: null,
};
const reducer = (state, action) => {
  console.log(action);
  switch (action.type) {
    case "ADD_TO_BASKET":
      return { ...state, basket: [...state.basket, action.payload] };
      break;
    case "REMOVE_FROM_BASKET":
      // const newState = state.basket.filter((id) => {
      //   return id !== state.basket.id;
      // });
      return { ...state };
      break;

    default:
      return state;
  }
};
export default reducer;
