import { PlaylistAddOutlined } from "@material-ui/icons";

export const initialState = {
  basket: [],
};
const reducer = (state, action) => {
  switch (action.type) {
    case "ADD_TO_BASKET":
      return [...state, (basket: payload.basket)];
      break;
    case "REMOVE_FROM_BASKET":
      break;

    default:
      return state;
  }
};
export default reducer;
