export const getBasketTotal = (basket) =>
  basket?.reduce((amount, item) => item.price + amount, 0);

export const initialState = {
  basket: [
    {
      id: "7423423",
      title:
        "lorem10Culpa elit voluptate deserunt duis sunt adipisicing sit esse minim aute sint.orem10Culpa elit voluptate deserunt",
      price: 1134,
      rating: 4,
      image:
        "https://images-na.ssl-images-amazon.com/images/I/71-2SRq1bKL._AC_SX679_.jpg",
    },
    {
      id: "8532",
      title:
        "lorem10Culpa elit voluptate deserunt duis sunt adipisicing sit esse minim aute sint.orem10Culpa elit voluptate deserunt",
      price: 1134,
      rating: 4,
      image:
        "https://images-na.ssl-images-amazon.com/images/I/71-2SRq1bKL._AC_SX679_.jpg",
    },
  ],
  user: null,
};
const reducer = (state, action) => {
  console.log(state.basket[0].price);
  switch (action.type) {
    case "ADD_TO_BASKET":
      return { ...state, basket: [...state.basket, action.payload] }; // action.payload=object
      break;
    case "REMOVE_FROM_BASKET":
      const newBasket = state.basket.filter((item) => {
        return item.id !== action.payload; // action.payload=id
      });
      return { ...state, basket: newBasket };
      break;

    default:
      return state;
  }
};
export default reducer;
